var React = require('react'),
  ReactDOM = require('react-dom'),
  App = require('./components/App'),
  log = require('./log');

var ui = {
  /*
   * Events generated by UI
   */
  eventHandlers: {
    // Select feed to see it's items
    onSelectFeed: [],
  },

  /*
   * Attach a listener to one of UI events
   */
  registerHandler: function(key, fn) {
    ui.eventHandlers[key].push(fn);
  },

  /*
   * Private
   *
   * UI events emitter
   */
  getHandler: function(key) {
    return function(data) {
      for(let h of ui.eventHandlers[key]) {
        h(data);
      }
    };
  },

  render: function(data) {
    ReactDOM.render(
      // jshint ignore:start
      <App feeds={data.feeds} onSelectFeed={this.getHandler('onSelectFeed')} />,
      // jshint ignore:end
      document.getElementById('root')
    );
  }
};

module.exports = ui;
